  def partOne() = {
    val bracketToCount = input.groupBy(c => c).mapValues(_.length)
    println(s"${bracketToCount.get('(').get - bracketToCount.get(')').get}")
  }

  def partTwo(input: String) = {
    def loop(pos: Int, floor: Int): Unit = {
      if (floor == -1) {
        println(pos)
      } else {
        input(pos) match {
          case '(' => loop(pos + 1, floor + 1)
          case ')' => loop(pos + 1, floor - 1)
        }
      }
    }
    loop(0, 0)
  }

  def main(args: Array[String]): Unit = {
    partTwo(input)
  }
